package de.snailshell.imageio.pic;import java.applet.Applet;import java.awt.*;import java.awt.image.MemoryImageSource;import java.io.BufferedInputStream;import java.net.URL;public class GEMPicture extends Applet {    String filename;    BufferedInputStream stream;    Image img;    Dimension d;    int pix[];    static final int Schwarz = 0xff000000;    static final int Weiss = 0xffffffff;    public void init() {        int i, bits, mask, offs;        filename = getParameter("file");        try {            stream = new BufferedInputStream(new URL(getDocumentBase(), filename).openStream());            pix = new int[640 * 400];            offs = 0;            for (i = 0; i < 32000; i++) {                bits = stream.read();                mask = 128;                while (mask > 0) {                    if ((bits & mask) > 0) {                        pix[offs++] = Schwarz;                    } else {                        pix[offs++] = Weiss;                    }                    mask >>= 1;                }            }            stream = null;            img = createImage(new MemoryImageSource(640, 400, pix, 0, 640));            pix = null;        } catch (Exception e) {        }    }    public void paint(Graphics g) {        d = size();        g.drawImage(img, 0, 0, d.width - 1, d.height - 1, null);    }    public String[][] getParameterInfo() {        String[][] info = {                {"file", "non-empty string", "The filename of a monochrome GEM 32000 bytes 640x400 *.PIC (*.DOO) image."}        };        return info;    }    public String getAppletInfo() {        return "GEMPicture by Thomas_Much@ka2.maus.de (27.08.1996)";    }}